ts.query:
A=fetch('elasticsearch.indices.primaries.merges.total', rate='counter', dropcounterresets="true")$global_tag_filter.tag_filter('cluster==$cluster').downsample('[[__interval]]','avg');
B=fetch('elasticsearch.indices.total.merges.total', rate='counter', dropcounterresets="true")$global_tag_filter.tag_filter('cluster==$cluster').downsample('[[__interval]]','avg');
AT=fetch('elasticsearch.indices.primaries.merges.total_time_in_millis', rate='counter', dropcounterresets="true")$global_tag_filter.tag_filter('cluster==$cluster').downsample('[[__interval]]','avg');
BT=fetch('elasticsearch.indices.total.merges.total_time_in_millis', rate='counter', dropcounterresets="true")$global_tag_filter.tag_filter('cluster==$cluster').downsample('[[__interval]]','avg');
prim_time=AT.div(A);
tot_time=BT.div(B);
output(prim_time, tot_time);
