mysql_buffer_pool_page_size=fetch('mysql.innodb.page_size.bytes')$global_tag_filter.downsample('[[__interval]]', 'avg').sum();
mysql_buffer_pool_free_pages=fetch('mysql.innodb.buffer_pool.pages')$global_tag_filter.tag_filter('state==free').downsample('[[__interval]]', 'avg').sum().mult(mysql_buffer_pool_page_size, ignore='state');
mysql_buffer_pool_data_pages=fetch('mysql.innodb.buffer_pool.pages')$global_tag_filter.tag_filter('state==data').downsample('[[__interval]]', 'avg').sum().mult(mysql_buffer_pool_page_size, ignore='state');
mysql_buffer_pool_misc_pages=fetch('mysql.innodb.buffer_pool.pages')$global_tag_filter.tag_filter('state==misc').downsample('[[__interval]]', 'avg').sum().mult(mysql_buffer_pool_page_size, ignore='state');
output(mysql_buffer_pool_free_pages, mysql_buffer_pool_data_pages, mysql_buffer_pool_misc_pages);
