ts.query:
mcr=fetch('elasticsearch.indices.byindex.primaries.request_cache.miss_count', rate='counter', dropcounterresets="true")$global_tag_filter.tag_filter("cluster=$cluster").tag_filter("index=$index").downsample('[[__interval]]', 'avg');
hcr=fetch('elasticsearch.indices.byindex.primaries.request_cache.hit_count', rate="counter", dropcounterresets="true")$global_tag_filter.tag_filter("cluster=$cluster").tag_filter("index=$index").downsample('[[__interval]]', 'avg');
tcr=hcr.add(mcr);
mcr1=fetch('elasticsearch.indices.byindex.total.request_cache.miss_count', rate='counter', dropcounterresets="true")$global_tag_filter.tag_filter("cluster=$cluster").tag_filter("index=$index").downsample('[[__interval]]', 'avg');
hcr1=fetch('elasticsearch.indices.byindex.total.request_cache.hit_count', rate="counter", dropcounterresets="true")$global_tag_filter.tag_filter("cluster=$cluster").tag_filter("index=$index").downsample('[[__interval]]', 'avg');
tcr1=hcr1.add(mcr1);
output(tcr,tcr1)
